mapper.update:
UPDATE
LEC_DATA

	LDT_TTL=#{ldtTtl}
	,LDT_CON=#{ldtCon}
	,LDT_WRITER=#{ldtWriter}

		, ATCH_FILE_ID = (SELECT MAX(ATCH_FILE_ID) FROM ATTACH)


WHERE
LDT_CD=#{ldtCd}

mapper.select:
	SELECT *
	FROM TEST T, STU_TEST ST
	WHERE T.TEST_CD = ST.TEST_CD
		AND T.LECA_CD = #{lecaCd}
        and t.test_cd = #{testCd}
        and st.stu_no = #{stuNo}

mapper.insert:

	SELECT NVL(MAX(TEST_CD),0)+1 FROM TEST					

INSERT INTO TEST(
				  TEST_CD
				 ,LECA_CD
				 ,TEST_NM 
                 ,TEST_CON
                 ,TEST_SDT
                 ,TEST_EDT
                 ,TEST_PART
                 )
		VALUES(
				  #{testCd}
				 ,#{lecaCd}
				 ,#{testNm}
				 ,#{testCon}
				 ,#{testSdt}
				 ,#{testEdt}
				 ,#{testPart}
		)

INSERT INTO TEST_Q(
			 TEQ_CD
			,TEST_CD
			,TEQ_SEQ
			,TEQ_OPTION1
			,TEQ_OPTION2
			,TEQ_OPTION3
			,TEQ_OPTION4
			,TEQ_ANSWER
			,TEQ_CON
) 
VALUES (
			 (SELECT NVL(MAX(TEQ_CD),0)+1 FROM TEST_Q)		
            ,#{testCd}
            ,(SELECT NVL(MAX(TEQ_SEQ),0)+1 FROM TEST_Q WHERE TEST_CD = #{testCd})
            ,#{teqOption1}
            ,#{teqOption2}
            ,#{teqOption3}
            ,#{teqOption4}
		    ,#{teqAnswer}
		    ,#{teqCon}
)


	SELECT NVL(MAX(ST_CD),0)+1 FROM STU_TEST

INSERT INTO STU_TEST (  
						 ST_CD
						,STU_NO
						,LECA_CD
						,TEST_CD
						,ST_SCORE
						,ST_YN
						,TEST_PART
						)
			  VALUES (   
			  			 #{stCd}
				  		,#{stuNo}
						,#{lecaCd}
						,#{testCd}
						,ROUND((#{stScore}/(5* (SELECT COUNT(TEQ_CD) TEQ_CD FROM TEST_Q WHERE TEST_CD = #{testCd})))*100)
						,'Y'
						,#{testPart}
					 )

mapper.select:
SELECT C.COL_NM ,D.DEP_NM ,S.STU_YR ,S.STU_NO ,S.STU_NM ,S.STU_TEL
FROM COLLEGE C, DEPARTMENT D, STUDENT S, STU_LEC SL
WHERE C.COL_CD = D.COL_CD AND D.DEP_CD = S.DEP_CD AND S.STU_NO = SL.STU_NO AND SL.LECA_CD = #{lecaCd}

SELECT COUNT(ATEN_CD) ATEN_CNT
FROM ATTENDENCE
WHERE ATEN_WK = #{atenWk}
AND ATEN_HR = #{atenHr}
AND LECA_CD = #{lecaCd}
AND STU_NO = #{stuNo}

mapper.update:
UPDATE ATTENDENCE
SET ATEN_CATE = #{atenCate}
WHERE ATEN_WK = #{atenWk}
AND ATEN_HR = #{atenHr}
AND LECA_CD = #{lecaCd}
AND STU_NO = #{stuNo}

UPDATE STU_LEC
SET SL_SCORE = #{slScore}
WHERE STU_NO = #{stuNo}
AND LECA_CD = #{lecaCd}
