mapper.update:

UPDATE APPROVAL
    SET 

    		APPR_DT = ''
    	   , EMP_NO = ''


    		APPR_DT = SYSDATE
    		, EMP_NO = #{empNo}


    		APPR_DT = SYSDATE
    		, EMP_NO = #{empNo}

      , APPR_RSN = #{apprRsn}
      , APPR_YN = #{apprYn}
WHERE APPR_CATE = #{apprCate}
AND APPR_TAG_CD = #{apprTagCd}

mapper.delete:

DELETE FROM APPROVAL
WHERE APPR_CATE = #{apprCate}
AND APPR_TAG_CD = #{apprTagCd}
