mapper.insert:
INSERT INTO COUNSEL (
  CNSL_CD
, STU_NO
, CNSL_CATE
, PRO_NO
, CNSL_TYPE
, CNSL_REG
, CNSL_TTL
, CNSL_CON
, CNSL_DT

)
VALUES(

SUBSTR(TO_NUMBER(TO_CHAR(SYSDATE,'YYYYMMDD')),1,6)||'000'||COUNSEL_SEQ.NEXTVAL

, #{stuNo}
, #{cnslCate}
, #{proNo}
, #{cnslType}
, SYSDATE

, #{cnslTtl}


, #{cnslCon}

, #{cnslDt,jdbcType=DATE}
)

mapper.update:
UPDATE COUNSEL 
SET
  CNSL_DT = SYSDATE 
, CNSL_RPL = #{cnslRpl}
, PRO_NO = #{proNo}
WHERE CNSL_CD = #{cnslCd}
