mapper.select:
SELECT  COUNT(U.LECA_CD)
FROM    LEC_APPLY U
WHERE   U.PRO_NO = #{empNo}

mapper.insert:

	SELECT NVL(MAX(LECA_CD),0)+1
	FROM LECTURE

INSERT INTO LECTURE (
             LECA_CD
            , SUB_CD
            , LECA_YN
) VALUES (
    #{lecaCd}
    , #{subCd}
    , 3
)

INSERT INTO LEC_APPLY (
             LECA_CD
            , LECA_YR
            , LECA_SEM
            , LECA_NM
            , LECA_CON
            , LECA_TRG
            , LECA_CRD
            , LECA_PER
            , LECA_CAP
            , LECA_TT
            , LECA_BOOK
            , LECA_NOTE
            , LECA_DT
            , LECA_GRADE
            , LECA_CATE
            , LECA_MP
            , LECA_FP
            , LECA_TP
            , LECA_AP
            , PRO_NO
) VALUES (
    (select max(LECA_CD) from lecture)
    , #{lecaYr}
    , #{lecaSem}
    , #{lecaNm}
    , #{lecaCon}
    , #{lecaTrg}
    , #{lecaCrd}
    , #{lecaCrd}
    , #{lecaCap}
    , #{lecaTt}
    , #{lecaBook}
    , #{lecaNote}
    , SYSDATE
    , #{lecaGrade}
    , #{lecaCate}
    , #{lecaMp}
    , #{lecaFp}
    , #{lecaTp}
    , #{lecaAp}
    , #{proNo}
)

update.foreach:
INTO WEEKPLAN(
WP_NO, LECA_CD, WP_CON
)VALUES(
    #{index}+1, (select max(LECA_CD) from lec_apply), #{weekPlan.wpCon}
)

