mapper.select:
SELECT  SUB_CD
FROM    SUBJECT
WHERE   SUB_NM = #{subNm}

mapper.update:
UPDATE  LECTURE
SET     SUB_CD = #{subCd}
		, LECA_YN = 0
WHERE   LECA_CD = #{lecaCd}

		UPDATE LEC_APPLY
		SET
            LECA_YR = #{lecaYr}
            , LECA_SEM = #{lecaSem}
            , LECA_NM = #{lecaNm}
            , LECA_CON = #{lecaCon}
            , LECA_TRG = #{lecaTrg}
            , LECA_CRD = #{lecaCrd}
            , LECA_PER = #{lecaCrd}
            , LECA_CAP = #{lecaCap}
            , LECA_TT =  #{lecaTt}
            , LECA_BOOK = #{lecaBook}
            , LECA_NOTE = #{lecaNote}
            , LECA_DT = SYSDATE
            , LECA_GRADE = #{lecaGrade}
            , LECA_CATE = #{lecaCate}
            , LECA_MP = #{lecaMp}
            , LECA_FP = #{lecaFp}
            , LECA_TP = #{lecaTp}
            , LECA_AP = #{lecaAp}
		WHERE LECA_CD = #{lecaCd}

update.foreach:

UPDATE WEEKPLAN
SET     WP_CON = #{weekPlan.wpCon}
WHERE   LECA_CD = #{weekPlan.lecaCd}
AND     WP_NO = #{weekPlan.wpNo}

