mapper.update:
UPDATE
TASK

	TASK_NM=#{taskNm}
	,TASK_CON=#{taskCon}
	,TASK_EDT=#{taskEdt}
	,TASK_SCORE=#{taskScore}

		, ATCH_FILE_ID = (SELECT MAX(ATCH_FILE_ID) FROM ATTACH)


WHERE
TASK_CD=#{taskCd}
AND LECA_CD=#{lecaCd}

mapper.delete:
DELETE
FROM TASK
WHERE
LECA_CD = #{lecaCd}
AND TASK_CD = #{taskCd}

mapper.select:
SELECT *
FROM TASK_SUBMIT
WHERE STU_NO = #{stuNo}
AND TASK_CD = #{taskCd}
AND LECA_CD = #{lecaCd}

mapper.insert:

	SELECT NVL(MAX(TSUB_CD),0)+1 FROM TASK_SUBMIT

insert into
TASK_SUBMIT (
			TSUB_CD
			,LECA_CD
			,TASK_CD
			,STU_NO
			,ATCH_FILE_ID
			,TSUB_CON
)
values(
			#{tsubCd}
			, #{lecaCd}
			, #{taskCd}
			, #{stuNo}
			, (SELECT MAX(ATCH_FILE_ID) FROM ATTACH)
			, #{tsubCon}
)


	SELECT NVL(MAX(TSUB_CD),0)+1 FROM TASK_SUBMIT

insert into
TASK_SUBMIT (
		TSUB_CD
		,LECA_CD
		,TASK_CD
		,STU_NO
		,TSUB_CON
)
values(
		#{tsubCd}
		, #{lecaCd}
		, #{taskCd}
		, #{stuNo}
		, #{tsubCon}
)

mapper.update:
UPDATE
TASK_SUBMIT

	TSUB_CON = #{tsubCon}

		, ATCH_FILE_ID = (SELECT MAX(ATCH_FILE_ID) FROM ATTACH)


WHERE
TSUB_CD = #{tsubCd}

UPDATE
TASK_SUBMIT
SET
TSUB_SCORE = #{tsubScore}
WHERE
TSUB_CD = #{tsubCd}

UPDATE LECTURE
    SET LEC_YN = #{lecYn}
      , LECA_YN = #{lecaYn}
WHERE LECA_CD = #{lecaCd}
