mapper.update:

UPDATE STUDENT S
SET      S.STU_YR = #{stuYr}
        , S.STU_SEM= #{stuSem}
        , S.STU_NM= #{stuNm}
        , S.STU_NME= #{stuNme}
        , S.STU_TEL= #{stuTel}
        , S.STU_ZIP= #{stuZip}
        , S.STU_ADDR1= #{stuAddr1}
        , S.STU_ADDR2= #{stuAddr2}
        , S.STU_BANK_CD= #{stuBankCd}
        , S.STU_DEPO= #{stuDepo}
        , S.STU_ACT= #{stuAct}

        , S.STU_PIC= #{stuPic}

        , S.STU_BIR= #{stuBir}
        , S.DEP_CD = (SELECT DEP_CD FROM DEPARTMENT WHERE DEP_CD=#{depCd})
WHERE S.STU_NO = #{stuNo}

mapper.select:
SELECT S.STU_NO , S.STU_NM , D.DEP_NM , R.REC_CD , R.REC_YR , R.REC_SEM , (SELECT C.COMD_NM FROM COMMON_DETAIL C WHERE C.COMD_CD = R.RGB_CD) AS RGB_CD , R.REC_PER , R.REC_RSN , R.REC_DT , C.CNSL_TTL , C.CNSL_CON , (SELECT C.COMD_NM FROM COMMON_DETAIL C WHERE C.COMD_CD = R.REC_YN) AS REC_YN , (SELECT C.COMD_NM FROM COMMON_DETAIL C WHERE C.COMD_CD = C.CNSL_YN) AS CNSL_YN , C.CNSL_RPL FROM STUDENT S, DEPARTMENT D, RECORD R, COUNSEL C WHERE S.DEP_CD = D.DEP_CD AND S.STU_NO = R.STU_NO AND R.REC_CD = C.REC_CD
AND R.RGB_CD = #{rgbCd}
AND R.REC_YN = #{recYn}
AND R.REC_YR LIKE '%' || #{recYr} || '%'
AND R.REC_SEM LIKE '%' || #{recSem} || '%'
AND S.STU_NM LIKE '%' || #{sutNm} || '%'

mapper.update:

UPDATE RECORD R
SET R.REC_YN = #{Yn}
	, R.REC_REJ = #{recRej}
WHERE R.REC_CD = #{recCd}

update student
set stu_rgb = (select comd_cd from common_detail where comd_nm = #{rgbCd})
where stu_no = #{stuNo}

