mapper.select:
SELECT MEM_NO 
FROM MEMBER LEFT OUTER JOIN STUDENT ON(MEM_NO = STU_NO) 
WHERE STU_NM = #{memNm}
AND STU_BIR = #{memBir}
AND STU_TEL = #{memTel}


UPDATE MEMBER
SET MEM_PASS = #{memPass}
WHERE MEM_TEL = #{memTel}
AND MEM_NO = #{memNo}

mapper.update:
UPDATE STUDENT
SET	 STU_ZIP = #{stuZip}
	, STU_ADDR1 = #{stuAddr1}
	, STU_ADDR2 = #{stuAddr2}
	, STU_BANK_CD = #{stuBankCd}
	, STU_ACT = #{stuAct}
WHERE STU_NO = #{stuNo}

UPDATE MEMBER
SET MEM_PASS = #{newPw}
WHERE MEM_PASS = #{oldPw}
AND MEM_NO = #{stuNo}
