mapper.select:
SELECT S.STU_NO , S.DEP_CD , S.STU_YR , S.STU_SEM , S.STU_NM , S.STU_NME , S.STU_TEL , S.STU_ZIP , S.STU_ADDR1 , S.STU_ADDR2 , S.STU_BANK_CD , S.STU_DEPO , S.STU_ACT , S.STU_PIC , S.STU_BIR , S.PRO_NO , E.EMP_NM FROM DEPARTMENT D, PROFESSOR P, EMPLOYEE E, STUDENT S WHERE D.DEP_CD = P.DEP_CD AND P.PRO_NO = E.EMP_NO AND P.PRO_NO = S.PRO_NO AND P.DEP_CD = #{depCd}
AND S.STU_NO LIKE '%' || #{stuNo} || '%'
AND S.STU_NM LIKE '%' || #{stuNm} || '%'
