mapper.delete:
DELETE FROM STU_LEC
WHERE STU_NO = #{stuNo}
AND LECA_CD = #{lecaCd}

DELETE FROM WISH
WHERE STU_NO = #{stuNo}
AND LECA_CD = #{lecaCd}

mapper.update:
UPDATE LECTURE
    SET LEC_HCNT = (SELECT LEC_HCNT FROM LECTURE WHERE LECA_CD = #{lecaCd}) + 1
WHERE LECA_CD = #{lecaCd}


UPDATE LECTURE
    SET LEC_HCNT = (SELECT LEC_HCNT FROM LECTURE WHERE LECA_CD = #{lecaCd}) - 1
WHERE LECA_CD = #{lecaCd}

mapper.select:
SELECT COUNT(*)
FROM LECTURE L
WHERE L.LECA_CD = #{lecaCd}
AND L.LEC_HCNT < (
    SELECT LA.LECA_CAP
    FROM LEC_APPLY LA
    WHERE LA.LECA_CD = #{lecaCd}
)

SELECT COUNT(*)
FROM DUAL
WHERE (
        SELECT CEIL(GC.RDC_REQ_CRD / 8)
        FROM GRDT_CND GC, DEPARTMENT D, STUDENT S
        WHERE GC.DEP_CD = D.DEP_CD
        AND S.DEP_CD = D.DEP_CD
        AND S.STU_NO = #{stuNo}
        ) > (
                SELECT LECA_CRD + (
                                    SELECT NVL(SUM(LA.LECA_CRD),0)
                                    FROM STU_LEC SL, LECTURE L, LEC_APPLY LA
                                    WHERE SL.LECA_CD = L.LECA_CD
                                    AND L.LECA_CD = LA.LECA_CD
                                    AND SL.STU_NO = #{stuNo}
                                )
                FROM LEC_APPLY
                WHERE LECA_CD = #{lecaCd}
             )
