mapper.select:
SELECT A.LECA_YR || '/' || A.LECA_SEM || '학기' LECA_CON, A.LECA_CD, A.LECA_NM, A.LECA_CATE, A.LECA_CRD, DECODE(A.LECA_GRADE, 1,'GRADE',0,'FAIL') LECA_GRADE, (SELECT COMD_NM FROM COMMON_DETAIL WHERE COMD_CON = TO_CHAR(S.SL_SCORE)) LECA_NOTE, S.SL_SCORE LECA_ROOM FROM STU_LEC S, LEC_APPLY A WHERE S.LECA_CD = A.LECA_CD
AND not (A.LECA_YR = #{year} AND A.LECA_SEM = #{semester})
AND A.LECA_YR = #{lecaYr} AND A.LECA_SEM = #{lecaSem}
AND S.STU_NO = #{stuNo}
ORDER BY A.LECA_YR DESC, A.LECA_SEM DESC, A.LECA_CD ASC

mapper.insert:

   		SELECT NVL(MAX(OBJ_CD),0)+1 FROM OBJECTION 

	INSERT INTO OBJECTION (
					    	 OBJ_CD
							,STU_NO
							,LECA_CD
							,OBJ_TTL
							,OBJ_CON
							,OBJ_DT
     					  )
     			  VALUES  (	 #{objCd}
		     			  	,#{stuNo}
		     			  	,#{lecaCd}
		     			  	,#{objTtl}
		     			  	,#{objCon}
		     			  	,SYSDATE
		     			  	)
